import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";

function Plastic() {
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  const apiUrl = 'http://localhost:4000/plastic';

  useEffect(() => {
    fetch(apiUrl, { method: "GET" })
      .then(response => response.json())
      .then(data => {
        setProduct(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err);
        setLoading(false);
      });
  }, []);

  const formatePro = product.map(item => (
    <article className="card" key={item.id}>
      <img src={item.image} alt={item.name} />
      <h2>{item.name}</h2>
      <p>Price: â‚¹{item.price}</p>
      <Link to={`/Plastic/${item.id}`}>Read More...</Link>
      <Link to={`/Update/${item.id}`}>Edit</Link>
      <button
        onClick={() => {
          fetch(`http://localhost:4000/plastic/remove/${item.id}`, { method: 'DELETE' })
            .then(() => navigate("/Plastic"));
        }}
      >
        Delete
      </button>
    </article>
  ));

  return (
    <section className="container getAll">
      <h1>Plastic Products</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="row">
          {formatePro}
        </div>
      )}
      {error && <p>Error: {error.message}</p>}
    </section>
  );
}

export default Plastic;
