import { useEffect, useState } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";

function Plastic() {
    const [product, setproduct] = useState([]);
    const { id } =useParams();
    const navigate = useNavigate();

    let apiUrl = 'http://localhost:4000/plastic'
    useEffect(() => {
        fetch(apiUrl, { method: "GET" })
            .then(response => response.json())
            .then(data => setproduct(data))
            .catch(err => console.error("Error fetching data: ", err));
    });


    const formatePro = product.map(item => {
        return (
            <div className="col-3 m-4">
                <div className="col">
                <div className="card" style={{ width: '18rem' }}>
                    <div className="divImage">
                        <img src={item.image} class="card-img-top mainImg" alt="..." />
                    </div>
                    <div className="card-body">
                        <h2 className="id">
                            id:
                            {item.id}
                        </h2>
                        <h5 className="card-title id">
                            name :-
                            {item.name}</h5>
                        <h5 className="id">
                            Price: â‚¹
                            {item.price}
                        </h5>
                    </div>
                        <Link to={'/Plastic/'+item.id} class="btn btn-outline-primary m-2" >Read More...</Link>
                        <Link to={'/Update/'+item.id} class="btn btn-outline-warning m-2" >Edit</Link>
                        <button  class="btn btn-outline-danger m-2" 
                            onClick={()=>{
                                console.log("delete");
                                console.log(item.id)
                                fetch('http://localhost:4000/plastic/remove/'+item.id, { method: 'DELETE' })
                                    .then((res)=>{navigate("/Plastic")});
                      }}
                        
                        >Delete</button>
                </div>
                </div>
            </div>
        )
    })


    return (
        <>
            <div className="container getAll">
                <div className="row">
                {formatePro}
                    </div>
                   
                </div>

        </>
    );
}

export default Plastic;
