import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";

function Plastic() {
  const [product, setProduct] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { id } = useParams();
  const navigate = useNavigate();

  const apiUrl = 'http://localhost:4000/plastic';

  useEffect(() => {
    fetch(apiUrl, { method: "GET" })
      .then(response => response.json())
      .then(data => {
        setProduct(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err);
        setLoading(false);
      });
  }, []);

  const formatePro = product.map(item => (
    <div className="col-md-4 mb-4" key={item.id}>
      <div className="card h-100">
        <img src={item.image} className="card-img-top img-fluid" alt={item.name} />
        <div className="card-body">
          <h5 className="card-title">{item.name}</h5>
          <p className="card-text">Price: â‚¹{item.price}</p>
        </div>
        <div className="card-footer">
          <Link to={`/Plastic/${item.id}`} className="btn btn-primary">Read More...</Link>
          <Link to={`/Update/${item.id}`} className="btn btn-warning">Edit</Link>
          <button
            className="btn btn-danger"
            onClick={() => {
              fetch(`http://localhost:4000/plastic/remove/${item.id}`, { method: 'DELETE' })
                .then(() => navigate("/Plastic"));
            }}
          >
            Delete
          </button>
        </div>
      </div>
    </div>
  ));

  return (
    <section className="container getAll">
      <h1>Plastic Products</h1>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="row">
          {formatePro}
        </div>
      )}
      {error && <p>Error: {error.message}</p>}
    </section>
  );
}

export default Plastic;
